import React, {useState} from 'react';
import logo from './styles/logo.svg';
import './styles/App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Modal, ModalBody, ModalHeader, ModalFooter, Button} from 'reactstrap';
import {Link} from 'react-router-dom';
import { applyMiddleware } from 'redux';

// const upload = require('./libs/storage')
// import multer from 'multer';

// const multer = require ('multer')
// const upload = multer({
//   dest: 'Storage/imgs'  
// })

function ListIntimacion() {

  const dataIntimacion = [
    { id: 1, 
      boletaintnum: "7106", 
      señorseñora: "Jose Romero",
      domiciliopart:"",
      lugaractuacion:"Santa Fé esquina Rivadavia",
      otorgaplazode:"48 hs",
      paracumplimientoa:"proceder a reparar la vereda en mal estado, conforme normativas vigentes",
      fechaintimacion:"06/06/2022",
      horaintimacion:"11:05",
      vencimientoint:"08/06/2022",
      notificadoint:"propuetario o responsable de Adrema Ausente",
      aclaracion:"se deja presente en la puerta de entrada",
      numcodigoint:"07/046",
      Inspectorint:"Ramos Carlos Alegre"
    },{ id: 2, 
        boletaintnum: "7106", 
        señorseñora: "Jose Romero",
        domiciliopart:"",
        lugaractuacion:"Santa Fé esquina Rivadavia",
        otorgaplazode:"48 hs",
        paracumplimientoa:"proceder a reparar la vereda en mal estado, conforme normativas vigentes",
        fechaintimacion:"06/06/2022",
        horaintimacion:"11:05",
        vencimientoint:"08/06/2022",
        notificadoint:"propuetario o responsable de Adrema Ausente",
        aclaracion:"se deja presente en la puerta de entrada",
        numcodigoint:"07/046",
        Inspectorint:"Ramos Carlos Alegre"
      },{ id: 3, 
        boletaintnum: "7106", 
        señorseñora: "Jose Romero",
        domiciliopart:"",
        lugaractuacion:"Santa Fé esquina Rivadavia",
        otorgaplazode:"48 hs",
        paracumplimientoa:"proceder a reparar la vereda en mal estado, conforme normativas vigentes",
        fechaintimacion:"06/06/2022",
        horaintimacion:"11:05",
        vencimientoint:"08/06/2022",
        notificadoint:"propuetario o responsable de Adrema Ausente",
        aclaracion:"se deja presente en la puerta de entrada",
        numcodigoint:"07/046",
        Inspectorint:"Ramos Carlos Alegre"
      },{ id: 4, 
        boletaintnum: "7106", 
        señorseñora: "Jose Romero",
        domiciliopart:"",
        lugaractuacion:"Santa Fé esquina Rivadavia",
        otorgaplazode:"48 hs",
        paracumplimientoa:"proceder a reparar la vereda en mal estado, conforme normativas vigentes",
        fechaintimacion:"06/06/2022",
        horaintimacion:"11:05",
        vencimientoint:"08/06/2022",
        notificadoint:"propuetario o responsable de Adrema Ausente",
        aclaracion:"se deja presente en la puerta de entrada",
        numcodigoint:"07/046",
        Inspectorint:"Ramos Carlos Alegre",
        fotosint:""
      },
  ];
//   const listainspecciones = [{
//     numexpediente:"xxxxxxx",
//     fechaentradinspec:"12/12/2020",
//     inspecfecha:"20/01/2021",
//     inspector:"xxxxxxx xxxxxxx",
//     fotoinspeccion:"Foto",
//     intimacion:"105025",
//     infracciones:"No Posee",
//     observaciones:"No Posee",
//     paseanumdestino:"xxxxxxxxxxxxxx",
//     fecha:"20/02/2021",
//     pasea:"xxxxxxx",
//     fechapasea:"20/08/2022"
// },{
//     numexpediente:"xxxxxxx",
//     fechaentradinspec:"12/12/2020",
//     inspecfecha:"20/01/2021",
//     inspector:"xxxxxxx xxxxxxx",
//     fotoinspeccion:"Foto",
//     intimacion:"105025",
//     infracciones:"No Posee",
//     observaciones:"No Posee",
//     paseanumdestino:"xxxxxxxxxxxxxx",
//     fecha:"20/02/2021",
//     pasea:"xxxxxxx",
//     fechapasea:"20/08/2022"
// },{
//     numexpediente:"xxxxxxx",
//     fechaentradinspec:"12/12/2020",
//     inspecfecha:"20/01/2021",
//     inspector:"xxxxxxx xxxxxxx",
//     fotoinspeccion:"Foto",
//     intimacion:"105025",
//     infracciones:"No Posee",
//     observaciones:"No Posee",
//     paseanumdestino:"xxxxxxxxxxxxxx",
//     fecha:"20/02/2021",
//     pasea:"xxxxxxx",
//     fechapasea:"20/08/2022"
// }]

  const [data, setData] = useState(dataIntimacion);
  const [modalEditar, setModalEditar] = useState(false);
  const [modalEliminar, setModalEliminar] = useState(false);
  const [modalInsertar, setModalInsertar] = useState(false);

  const [ExpedienteSeleccionado, setExpedienteSeleccionado] = useState({
        id: 4, 
        boletaintnum: "7106", 
        señorseñora: "Jose Romero",
        domiciliopart:"",
        lugaractuacion:"Santa Fé esquina Rivadavia",
        otorgaplazode:"48 hs",
        paracumplimientoa:"proceder a reparar la vereda en mal estado, conforme normativas vigentes",
        fechaintimacion:"06/06/2022",
        horaintimacion:"11:05",
        vencimientoint:"08/06/2022",
        notificadoint:"propuetario o responsable de Adrema Ausente",
        aclaracion:"se deja presente en la puerta de entrada",
        numcodigoint:"07/046",
        Inspectorint:"Ramos Carlos Alegre",
        fotosint:""
  });

  const seleccionarIntimacion=(elemento, caso)=>{
setIntimacionSeleccionado(elemento);
(caso==='Editar')?setModalEditar(true):setModalEliminar(true)
  }

  const handleChange=e=>{
    const {name, value}=e.target;
    setIntimacionSeleccionado((prevState)=>({
      ...prevState,
      [name]: value
    }));
  }

  const editar =()=>{
    var dataNueva=data;
    dataNueva.map(intimacion=>{
      if(intimacion.id===IntimacionSeleccionado.id){
        intimacion.boletaintnum=IntimacionSeleccionado.boletaintnum;
        intimacion.señorseñora=IntimacionSeleccionado.señorseñora;
        intimacion.domiciliopart=IntimacionSeleccionado.domiciliopart;
        intimacion.lugaractuacion=IntimacionSeleccionado.lugaractuacion; 
        intimacion.otorgaplazode=IntimacionSeleccionado.otorgaplazode;
        intimacion.paracumplimientoa=IntimacionSeleccionado.paracumplimientoa;
        intimacion.fechaintimacion=IntimacionSeleccionado.fechaintimacion;
        intimacion.horaintimacion=IntimacionSeleccionado.horaintimacion;
        intimacion.vencimientoint=IntimacionSeleccionado.vencimientoint;
        intimacion.notificadoint=IntimacionSeleccionado.notificadoint;
        intimacion.aclaracion=IntimacionSeleccionado.aclaracion;
        intimacion.numcodigoint=IntimacionSeleccionado.numcodigoint;
        intimacion.Inspectorint=IntimacionSeleccionado.Inspectorint;
        intimacion.fotosint=IntimacionSeleccionado.fotosint;
      }
    });  
    setData(dataNueva);
    setModalEditar(false);
    }
  

  const eliminar =()=>{
    setData(data.filter(intimacion=>intimacion.id!==ExpedienteSeleccionado.id));
    setModalEliminar(false);
  }

  const abrirModalInsertar=()=>{
    setExpedienteSeleccionado(null);
    setModalInsertar(true);
  }

  const insertar =()=>{
    var valorInsertar=ExpedienteSeleccionado;
    valorInsertar.id=data[data.length-1].id+1;
    var dataNueva = data;
    dataNueva.push(valorInsertar);
    setData(dataNueva);
    setModalInsertar(false);
  }

  return (
    
    <div className="App">
                    <a href="/home"><img height="50" src={require('./images/logoMuni.png')} /></a><br/>    
                    {/* <img src='https://ciudaddecorrientes.gov.ar/sites/default/themes/ciudaddecorrientes/logo.png' alt="to home" /> */}
                
                    <h1 className="colorLetrasGris">SECCIÓN DE CARGA DE INSPECCIONES</h1>                    
                    <img className='logocorrientes' height="200" src={require('./images/fileserver.jpg')} /> <br/>
                    {/* <select className="selectfont">
                        <option value="" selected disabled hidden>ORDENAR</option>                
                        <option value='asc'>Fecha</option>
                        <option value='desc'>Estado</option>
                        <option value='desc'>Fecha Inicio Expediente</option>
                        <option value='desc'>Fecha Plano Registrado</option>
                    </select>        */}
                    <Link to= '/ListInfraccion'><Button  color='secondary'>Lista Instimaciones</Button></Link> {" - "} 
                    <Link to= '/ListInfraccion'><Button  color='secondary'>Lista Multas</Button></Link> {" - "} 
                    <button className="btn btn-success" onClick={()=>abrirModalInsertar()}>Crear Intimación</button> {" - "}
                    <button className="btn btn-danger" onClick={()=>abrirModalInsertar()}>Crear Multa</button> {" - "}
                    <Link to= '/Home'><Button color='primary'>Volver Menu Principal</Button></Link> {" - "} 
                    <img src={require('./images/separadorpagina.png')} />
      {/* <h2>Países en los que la gente pasa más tiempo en redes sociales (2019)</h2> */}
      {/* <br /> */}
    
    <br /><br />
      <table className="table table-bordered">
        <thead>
          <tr>
            {/* <th>ID</th> */}
            <th>numero de expediente</th>
            <th>Intimación</th>
            <th>Fecha Inspección</th>
            <th>Inspector</th>
            <th>Vencimiento Intimación</th>
          </tr>
        </thead>
        <tbody>
          {data.map(elemento=>(
            <tr>
              {/* <td>{elemento.id}</td> */}
              <td>{elemento.numexpediente}</td>
              <td>{elemento.intimacion}</td>
              <td>{elemento.inspecfecha}</td>
              <td>{elemento.inspector}</td>
              <td>{elemento.vencintimacion}</td>
              <td><button className="btn btn-primary" onClick={()=>seleccionarPais(elemento, 'Editar')}>Editar</button> {"   "} 
              <button className="btn btn-danger" onClick={()=>seleccionarPais(elemento, 'Eliminar')}>Eliminar</button></td>
            </tr>
          ))
          }
        </tbody>
      </table>

      <Modal isOpen={modalEditar}>
        <ModalHeader>
          <div>
            <h3>Editar Intimación</h3>
          </div>
        </ModalHeader>
        <ModalBody>
          <div className="form-group">
            {/* <label>ID</label>
            <input
              className="form-control"
              readOnly
              type="text"
              name="id"
              value={ExpedienteSeleccionado && ExpedienteSeleccionado.id}
            /> */}
            <br />

            <label>Expediente</label>
            <input
              className="form-control"
              type="text"
              name="numexpediente"
              readOnly
              value={ExpedienteSeleccionado && ExpedienteSeleccionado.numexpediente}
              onChange={handleChange}
            />
            <br />

            <label>Intimación</label>
            <input
              className="form-control"
              type="text"
              name="intimacion"
              readOnly
              value={ExpedienteSeleccionado && ExpedienteSeleccionado.intimacion}
              onChange={handleChange}
            />
            <br />
            <label>Fecha Intimación</label>
            <input
              className="form-control"
              type="text"
              name="inspecfecha"
              readOnly
              value={ExpedienteSeleccionado && ExpedienteSeleccionado.inspecfecha}
              onChange={handleChange}
            />
            <br />
            <label>Inspector</label>
            <input
              className="form-control"
              type="text"
              name="inspector"
              value={ExpedienteSeleccionado && ExpedienteSeleccionado.inspector}
              onChange={handleChange}
            />
            <br />
            <label>Vencimiento Intimación</label>
            <input
              className="form-control"
              type="text"
              name="inspector"
              value={ExpedienteSeleccionado && ExpedienteSeleccionado.vencintimacion}
              onChange={handleChange}
            />
            
            <br />
          </div>
        </ModalBody>
        <ModalFooter>
         <button className="btn btn-success" onClick={()=>editar()}>
            Modo Admin
          </button>
          <button className="btn btn-primary" onClick={()=>editar()}>
            Actualizar
          </button>
          <button
            className="btn btn-danger"
            onClick={()=>setModalEditar(false)}
          >
            Cancelar
          </button>
        </ModalFooter>
      </Modal>


      <Modal isOpen={modalEliminar}>
        <ModalBody>

          Estás Seguro que deseas eliminar la Intimación Nº {ExpedienteSeleccionado && ExpedienteSeleccionado.intimacion}
          <br/>
          del Expediente Nº {ExpedienteSeleccionado && ExpedienteSeleccionado.numexpediente}
        </ModalBody>
        <ModalFooter>
          <button className="btn btn-danger" onClick={()=>eliminar()}>
            Sí
          </button>
          <button
            className="btn btn-secondary"
            onClick={()=>setModalEliminar(false)}
          >
            No
          </button>
        </ModalFooter>
      </Modal>


        <Modal isOpen={modalInsertar}>
        <ModalHeader>
          <div>
            <h3>Boleta Intimación</h3>
          </div>
        </ModalHeader>
        <ModalBody>
          <div className="form-group">
            {/* <label>ID</label>
            <input
              className="form-control"
              readOnly
              type="text"
              name="id"
              value={data[data.length-1].id+1}
            /> */}
            <br />
            <label>Expediente Nº</label>
            <input
              className="form-control"
              type="text"
              name="numexpediente"
              value={ExpedienteSeleccionado ? ExpedienteSeleccionado.numexpediente: ''}
              onChange={handleChange}
            />
            <br />
            <label>Intimación Nº</label>
            <input
              className="form-control"
              type="text"
              name="intimacion"
              value={ExpedienteSeleccionado ? ExpedienteSeleccionado.intimacion: ''}
              onChange={handleChange}
            />
            <br />
            <label>Fecha Intimación (mes/dia/año)</label>
            <input
              className="form-control"
              type="text"
              name="inspecfecha"
              value={ExpedienteSeleccionado ? ExpedienteSeleccionado.inspecfecha: ''}
              onChange={handleChange}
            />
            <br />
            <label>Inspector</label>
            <input
              className="form-control"
              type="text"
              name="inspector"
              value={ExpedienteSeleccionado ? ExpedienteSeleccionado.inspector: ''}
              onChange={handleChange}
            />
            <br />
            <label>Vencimiento Intimación</label>
            <input
              className="form-control"
              type="text"
              name="vencintimacion"
              value={ExpedienteSeleccionado ? ExpedienteSeleccionado.vencintimacion: ''}
              onChange={handleChange}
            />
            <br />
            <label>Subir Fotos:</label>
            <input
              className="form-control"
              type="file"
              name="fotoinspeccion"
              value={ExpedienteSeleccionado ? ExpedienteSeleccionado.fotoinspeccion:''}
              onChange={handleChange}
            />
          </div>
        </ModalBody>
        <ModalFooter>
          <button className="btn btn-primary"
          onClick={()=>insertar()}>
            Insertar
          </button>
          <button
            className="btn btn-danger"
            onClick={()=>setModalInsertar(false)}
          >
            Cancelar
          </button>
        </ModalFooter>
      </Modal>
    </div>
  );
}

export default ListIntimacion;
